export class TestConfig {
  constructor(config = {}) {
    this.browserName = config.browserName || 'chromium';
    this.headless = config.headless ?? true;
    this.slowMo = config.slowMo || 0;
    this.timeout = config.timeout || 30000;
    this.viewport = config.viewport || { width: 1280, height: 720 };
    this.baseURL = config.baseURL || '';
    this.recordVideo = config.recordVideo ?? false;
    this.screenshot = config.screenshot || 'only-on-failure';
  }

  static fromEnv() {
    return new TestConfig({
      browserName: process.env.BROWSER || 'chromium',
      headless: process.env.HEADLESS !== 'false',
      slowMo: parseInt(process.env.SLOW_MO || '0'),
      timeout: parseInt(process.env.TIMEOUT || '30000'),
      baseURL: process.env.BASE_URL || '',
      recordVideo: process.env.RECORD_VIDEO === 'true',
      screenshot: process.env.SCREENSHOT || 'only-on-failure'
    });
  }
}